package com.doberan.study.myapplication;import android.support.v4.view.ViewPager;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import com.doberan.study.myapplication.adapter.SamplePagerAdapter;public class MainActivity extends AppCompatActivity {    private ViewPager mViewPager = null;    private SamplePagerAdapter mSamplepagerAdapter = null;    private int mPagerPosition = 0;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        setContents();    }    public void setContents() {        mSamplepagerAdapter = new SamplePagerAdapter();        mViewPager = findViewById(R.id.main_pager);        mViewPager.setAdapter(mSamplepagerAdapter);        mViewPager.setCurrentItem(mPagerPosition, false);        mViewPager.addOnPageChangeListener(new ViewPager.OnPageChangeListener() {            @Override            public void onPageScrolled(int position, float positionOffset, int positionOffsetPixels) {            }            @Override            public void onPageSelected(int position) {                mPagerPosition = position;                mViewPager.setCurrentItem(position);            }            @Override            public void onPageScrollStateChanged(int state) {            }        });    }}